mapper.update:
UPDATE quality_base_test_item
SET
name = #{name},
unit = #{unit}
WHERE ID = #{id}

mapper.select:
SELECT a.id AS id, a.name AS name,a.unit AS unit
FROM quality_base_test_item AS a
WHERE a.id = #{id}

SELECT * FROM jc_integration_db.quality_base_test_item as qbti
WHERE qbti.id IN 
#{item}

SELECT a.id AS id, a.name AS name,a.unit AS unit
FROM quality_base_test_item AS a
ORDER BY ID ASC;
